/**
 * Get color configuration for a language
 * Falls back to default colors if language is not found
 */
export function getLanguageColors(language: string): LanguageColorConfig {
	const normalizedLanguage = language.toLowerCase().trim();

	return (
		languageColors[normalizedLanguage] || {
			bg: 'bg-gray-500/20',
			border: 'border-gray-500/50',
			text: 'text-gray-600 dark:text-gray-400',
		}
	);
}

/**
 * Get all available language colors (useful for previews or testing)
 */
export function getAllLanguageColors(): Record<string, LanguageColorConfig> {
	return languageColors;
}

/**
 * Get all supported languages
 */
export function getSupportedLanguages(): string[] {
	return Object.keys(languageColors).sort();
}

/**
 * Get the total count of supported languages
 */
export function getSupportedLanguagesCount(): number {
	return Object.keys(languageColors).length;
}
